// FileName: AddTransaction.js 
import React from "react";
import styled from "styled-components"; 
const Container = styled.div` 
text-align: center; 
border: 1px solid #000; 
padding: 20px; 
border-radius: 5px; 
margin-bottom: 25px; 
`; 

const Input = styled.input` 
width: 100%; 
padding: 15px 0px; 
outline: none; 
border-radius: 5px; 
margin: 5px 0; 
border: 1px solid #000; 
`; 

const RadioContainer = styled.div` 
display: flex; 
align-items: center; 
justify-content: center; 
`; 

const Label = styled.label` 
margin-left: 10px; 
cursor: pointer; 
`; 

const RadioBtn = styled(RadioContainer)` 
margin: 10px 20px 10px 0; 
`; 

const SubmitBtn = styled.button` 
background-color: #44E610; 
color: #fff; 
border-radius: 5px; 
padding: 10px 20px; 
border: none; 
outline: none; 
cursor: pointer; 
&:hover { 
	background-color: #44E610; 
} 
`; 
		export default function AddTransaction({
			details,
			setDetails,
			amount,
			setAmount,
			category,
			setCategory,
			categories,
			transType,
			setTransType,
			setToggle,
			chartData,
			setChartData,
			transactions,
            setTransactions	
		})

		{

				return ( 
					<Container>
				
					<Input 
						type={"text"} 
						placeholder="Enter Details"
						value={details} 
						onChange={(e) => setDetails(e.target.value)} 
					/> 
					<Input 
						type={"number"} 
						placeholder="Enter Amount"
						value={amount} 
						onChange={(e) => setAmount(e.target.value)} 
					/> 
					
					 <select style={{width : '101%', padding : '15px 0px' , border: '1px solid #000'
					 , margin: '5px 0', outline:'none', borderRadius:'5px'}}
						  onChange={(e) => setCategory(e.target.value)} >
							   <option>Select a category</option>
								selectedValue={category}
								{categories.map((category, index) => {
									return (
										<option key={index}>
										 {category}
										</option>
									);
								})}
					</select>	
				
					<RadioContainer> 
						<RadioBtn> 
						<input 
							type="radio"
							id="expense"
							name="type"
							value={"expense"} 
							checked={transType === "expense"} 
							onChange={(e) => setTransType(e.target.value)} 
						/> 
						<Label htmlFor="expense">Expense</Label> 
						</RadioBtn> 
				
						<RadioBtn> 
						<input 
							type="radio"
							id="income"
							name="type"
							value={"income"} 
							checked={transType === "income"} 
							onChange={(e) => setTransType(e.target.value)} 
						/> 
						<Label htmlFor="income">Budget</Label> 
						</RadioBtn> 
					</RadioContainer> 
				
		<SubmitBtn onClick={() => { 
		
        let amountNumber = parseInt(amount);
		if (amountNumber <= 0 || details === "") {
			alert("Please enter a valid amount and name");
			return;
		}	

        setTransactions([
            ...transactions,
            {
                id: new Date().getTime(),
                category,
                details,
                amount: amountNumber,
                transType
            },
        ]);
			if(transType==="expense"){
		let newChartData = [...chartData];
		let index = newChartData.findIndex(
			(item) => item.name === category
		);
		newChartData[index].amount += amountNumber;
		setChartData(newChartData);
	}
	
		setToggle(false);
						setDetails("");
						setAmount("");
						setCategory("");
	}}
					
					>  Add	</SubmitBtn>

					<SubmitBtn
					onClick={() => {
						setToggle(false);
					}}
					title="Cancel"
				>Cancel</SubmitBtn>			
					</Container> 
					
				); 
			
			}
