import React, { useEffect, useState } from 'react';
import firebase from 'firebase/compat/app';
import 'firebase/firestore'; 
import 'firebase/compat/auth';
import { BrowserRouter as Router, Route, Switch, Redirect } from 'react-router-dom';
import SignUp from './components/SignUp';
import Login from './components/Login';
import Dashboard from './components/Dashboard';

// Initialize Firebase
const firebaseConfig = {
  apiKey: "AIzaSyDFfzkgHF85Gkcm6J-6ZCTYhxQEXCpQha0",
  authDomain: "myexpense-34928.firebaseapp.com",
  projectId: "myexpense-34928",
  storageBucket: "myexpense-34928.appspot.com",
  messagingSenderId: "866255289545",
  appId: "1:866255289545:web:a070628b004337492c6118",
  measurementId: "G-FWTD0WYGNL"
};

firebase.initializeApp(firebaseConfig);

function App() {
  const [user, setUser] = useState(null);

  useEffect(() => {
    const unsubscribe = firebase.auth().onAuthStateChanged((user) => {
      setUser(user);
    });

    return unsubscribe;
  }, []);

  const signUp = async (email, password) => {
    try {
      await firebase.auth().createUserWithEmailAndPassword(email, password);
    } catch (error) {
      console.error('Error signing up:', error.message);
      throw error;
    }
  };

  const signIn = async (email, password) => {
    try {
      await firebase.auth().signInWithEmailAndPassword(email, password);
    } catch (error) {
      console.error('Error signing in:', error.message);
      throw error;
    }
  };

  const signOut = async () => {
    try {
      await firebase.auth().signOut();
    } catch (error) {
      console.error('Error signing out:', error.message);
    }
  };

  return (
    <div className="App">
      <Router>
        <Switch>
          <Route path="/signup">
            {user ? <Redirect to="/dashboard" /> : <SignUp signUp={signUp} />}
          </Route>
          <Route path="/login">
            {user ? <Redirect to="/dashboard" /> : <Login signIn={signIn} />}
          </Route>
          <Route path="/dashboard">
            {user ? <Dashboard user={user} signOut={signOut} /> : <Redirect to="/login" />}
          </Route>
          <Route exact path="/">
            <Redirect to="/dashboard" />
          </Route>
        </Switch>
      </Router>
    </div>
  );
}

export default App;
