// FileName: Tracker.js 

import React, { useEffect, useState } from "react"; 
import styled from "styled-components"; 
import AddTransaction from "./AddTransaction"; 
import OverviewComponent from "./OverviewComponent"; 
import TransactionsContainer from "./TransactionsContainer"; 
import { PieChart, Pie ,Cell,Tooltip,Legend} from "recharts";

const Container = styled.div` 
display: flex; 
flex-direction: column; 
width: 600px; 
max-width: 100%; 
background-color: #fff; 
padding: 30px 20px; 
border: 1px solid #000; 
border-radius: 5px; 
margin: 10px; 
`; 

const Heading = styled.h1` 
font-size: 30px; 
font-weight: bold; 
text-align: center; 
margin-bottom: 20px; 
`; 

const TransactionDetails = styled.div` 
display: flex; 
justify-content: space-between; 
align-items: center; 
gap: 20px; 
margin-bottom: 25px; 
`; 

const THeading = styled.div` 
font-size: 30px; 
font-weight: bold; 
text-align: center; 
margin-bottom: 20px; 
color: #44E610; 
`; 

const ExpenseBox = styled.div` 
flex: 1; 
border: 1px solid #000; 
border-radius: 5px; 
padding: 10px 20px; 
background-color: #fff; 
& span { 
	font-weight: bold; 
	font-size: 25px; 
	display: block; 
	color: ${(props) => (props.isExpense ? "red" : "green")}; 
} 
`; 

const IncomeBox = styled(ExpenseBox)``; 
const Tracker = () => { 
const [toggle, setToggle] = useState(false); 
const [transactions, setTransactions] = useState([]); 
const [expense, setExpense] = useState(0); 
const [income, setIncome] = useState(0); 
const [amount, setAmount] = useState(""); 
const [details, setDetails] = useState(""); 
const [category, setCategory] =   useState("Food");
const categories = ["Food", "Clothes", "Bills","Groceries","Others"];
const [transType, setTransType] = useState("expense"); 
const COLORS = ["#8884d8", "#82ca9d", "#FFBB28", "#FF8042", "#AF19FF"];
	
		const [chartData,setChartData] = useState([
			{
			   name: "Food",
			   amount: 0,
			},
			{
			   name: "Clothes",
			   amount: 0
			},
			{
			   name: "Bills",
			   amount: 0
			},
			{
			   name: "Groceries",
			   amount: 0
			},
			{
			   name: "Others",
			   amount: 0
			}
		 ]);

	const CustomTooltip = ({ active, payload, label }) => {
			if (active) {
			   return (
			   <div
				  className="custom-tooltip"
				  style={{
					 backgroundColor: "#ffff",
					 padding: "5px",
					 border: "1px solid #cccc"
				  }}
			   >
				  <label>{`${payload[0].name} : ${payload[0].value}`}</label>
			   </div>
			);
		 }
		 return null;
	  };
/*
const AddTransactions = (payload) => { 
	const transactionArray = [...transactions]; 
	transactionArray.push(payload); 
	setTransactions(transactionArray);
	
}; 
*/
const removeTransaction = (id) => { 
	const updatedTransactions = transactions 
								.filter((transaction) => transaction.id !== id); 
	setTransactions(updatedTransactions); 

}; 

const calculateTransactions = () => { 
	let exp = 0; 
	let inc = 0; 

	transactions.map((item) => { 
return	item.transType === "expense"
		? (exp = exp + item.amount) 
		: (inc = inc + item.amount); 
	}); 

	setExpense(exp); 
	setIncome(inc); 

}; 

useEffect(() => { 
	calculateTransactions(); 
} ); 

return ( 
	<Container> 
 	   
		<THeading>Expense Tracker</THeading> 
	<Heading></Heading> 
	


	
	<OverviewComponent 
		toggle={toggle} 
		setToggle={setToggle} 
		expense={expense} 
		income={income}
			
	/> 

<PieChart width={330} height={300}>
      <Pie
         data={chartData}
         color="#000000"
         dataKey="amount"
         nameKey="name"
         cx="50%"
         cy="50%"
         outerRadius={120}
         fill="#8884d8"
      >
		      {chartData.map((entry, index) => (
            <Cell
               key={`cell-${index}`}
               fill={COLORS[index % COLORS.length]}
            />
         ))}
        </Pie>
		<Tooltip content={<CustomTooltip />} />
		<Legend />
      </PieChart>

		{toggle && ( 
		<AddTransaction 
		setToggle={setToggle} 
		//AddTransactions={AddTransactions} 

		details={details}
		setDetails={setDetails}
		amount={amount}
		setAmount={setAmount}
		category={category}
		setCategory={setCategory}
		transType={transType}
		setTransType={setTransType}
		categories={categories}
		chartData={chartData}
		setChartData={setChartData}
		transactions={transactions}
		setTransactions={setTransactions}

				/> 
	)} 

	<TransactionDetails> 
		<ExpenseBox isExpense> 
		Expense <span>${expense}</span> 
		</ExpenseBox> 
		<IncomeBox> 
		Budget/Income <span>${income}</span> 
		</IncomeBox> 

	</TransactionDetails> 

	<TransactionsContainer 
		transactions={transactions} 
		removeTransaction={removeTransaction} 
		chartData={chartData}		
	/> 
	</Container> 
); 
}; 

export default Tracker
